mapper.update:
update partner_content
   set pc_content = #{pcContent},
		pc_file = #{pcFile},
		pc_idx = #{pcIdx}
 where pc_idx = #{pcIdx}

mapper.delete:
update partner_content
   set deleted_yn = 'Y' 
 where pc_idx = #{pcIdx}

update partner_detail_comment
   set comment_deleted_yn = 'Y'  
 where pdc_idx = #{pdcIdx}

mapper.update:
update partner_detail 
set progress = '1'
where pd_idx = #{pdIdx}
